import React, { useState, useEffect, useContext } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { AppContext } from '../App';

const Homepage = () => {
    const { breed, setBreed } = useContext(AppContext);
    const [cats, setCats] = useState([]);

    useEffect(() => {
      // Fetch cats data based on selected breed
      const fetchCats = async () => {
        try {
          const response = await axios.get(`https://api.thecatapi.com/v1/images/search?limit=5&breed_id=${breed}`);
          setCats(response.data);
        } catch (error) {
          console.error(error);
        }
      };

      fetchCats();
    }, [breed]);

    // Render cats data
    const renderCats = () => {
      if (cats.length === 0) {
        return <div>Loading cats...</div>;
      }

      return (
        <div>
          <h1>Homepage</h1>
          <h2>Breed: {breed}</h2>
          <div>
            {cats.map(cat => (
              <div key={cat.id}>
                <img src={cat.url} alt={`Cat ${cat.id}`} width="300" height="200" />
                <Link to={`/cats/${cat.id}`}>View Details</Link>
              </div>
            ))}
          </div>
        </div>
      );
    };

    return renderCats();
  };

  export default Homepage;
