import React, { useContext, useEffect, useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import axios from 'axios';
import { AppContext } from '../App';

const CatSinglePage = () => {
const { catId } = useParams();
const { breed,setBreed } = useState('');
const [cat, setCat] = useState(null);

useEffect(() => {
  // Fetch cat data based on cat ID
  const fetchCat = async () => {
      try {
      const response = await axios.get(`https://api.thecatapi.com/v1/images/${catId}`);
      setCat(response.data);
      console.log(response.data.breeds[0]);
    } catch (error) {
      console.error(error);
    }
  };

  fetchCat();
}, [catId]);

// Render cat data
const renderCat = () => {
  if (!cat) {
    return <div>Loading cat...</div>;
  }

  return (
    <div>
      <Link to={`/`}>Back to Homepage</Link>
      <h2>Breed: {breed}</h2>
      <div>
        <figure>
          <img src={cat.url} alt={`Cat ${cat.id}`} width="300" height="200" />
        </figure>
      </div>
    </div>
  );
};

return renderCat();
};

export default CatSinglePage;
