import React, { useContext, useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { AppContext } from '../App';

const Homepage = () => {
  const { breed } = useContext(AppContext);
  const [cats, setCats] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [loadedCount, setLoadedCount] = useState(0); // Track the count of loaded cats

  useEffect(() => {
    // Fetch cats based on selected breed
    const fetchCats = async () => {
      setIsLoading(true);
      try {
        const response = await axios.get(`https://api.thecatapi.com/v1/images/search?breed_ids=${breed}&limit=10`);
        setCats(prevCats => [...prevCats, ...response.data]);
        setIsLoading(false);
      } catch (error) {
        setError(error);
        setIsLoading(false);
      }
    };

    fetchCats();
  }, [breed]);

  // Load more cats
  const handleLoadMore = async () => {
    if (isLoading) {
      return;
    }

    setIsLoading(true);
    try {
      const response = await axios.get(`https://api.thecatapi.com/v1/images/search?breed_ids=${breed}&limit=10`);
      setCats(prevCats => [...prevCats, ...response.data]);
      setLoadedCount(prevCount => prevCount + 10); // Update the count of loaded cats
      setIsLoading(false);
    } catch (error) {
      setError(error);
      setIsLoading(false);
    }
  };

  // Render cats
  const renderCats = () => {
    if (isLoading) {
      return <div>Loading cats...</div>;
    }

    if (error) {
      return <div>Error loading cats: {error.message}</div>;
    }

    return (
      <div>
        <h1>Homepage</h1>
        <h2>Breed: {breed}</h2>
        <div>
          {cats.map(cat => (
            <div key={cat.id}>
              <img src={cat.url} alt={`Cat ${cat.id}`} width="200" height="150" />
              <p>ID: {cat.id}</p>
              <p>URL: {cat.url}</p>
            </div>
          ))}
        </div>
        {loadedCount < cats.length && (
          <button onClick={handleLoadMore} disabled={isLoading}>
            {isLoading ? 'Loading more...' : 'Load More'}
          </button>
        )}
        <Link to={`/cat/${cats[0]?.id}`}>View Cat Details</Link>
      </div>
    );
  };

  return renderCats();
};

export default Homepage;
