import React, { useContext, useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios'; // If you prefer fetch API, you can use fetch() instead

const CatSinglePage = () => {
  const { catId } = useParams();
  const [cat, setCat] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { breed } = useContext(AppContext);

  useEffect(() => {
    // Fetch detailed information about the selected cat on component mount
    const fetchCat = async () => {
      try {
        const response = await axios.get(`https://api.thecatapi.com/v1/images/${catId}`);
        setCat(response.data);
      } catch (error) {
        setError('Failed to fetch cat details. Please try again later.');
      } finally {
        setLoading(false);
      }
    };
    fetchCat();
  }, [catId]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h1>Single Cat Page</h1>
      {cat && (
        <div>
          <img src={cat.url} alt={`Cat ${catId}`} />
          <h2>Breed name: {cat.breeds[0].name}</h2>
          <p>Origin: {cat.breeds[0].origin}</p>
          <p>Temperament: {cat.breeds[0].temperament}</p>
          <p>Description: {cat.breeds[0].description}</p>
          <Link to="/">Back</Link>
        </div>
      )}
    </div>
  );
};

export default CatSinglePage
