import React, { useContext, useEffect, useState } from 'react';
import { Link, useParams, useHistory } from 'react-router-dom';
import axios from 'axios';
import { AppContext } from '../App';

const CatSinglePage = () => {
  const { id } = useParams();
  const { breed } = useContext(AppContext);
  const [cat, setCat] = useState(null);
  const history = useHistory();

  useEffect(() => {
    // Fetch cat data based on cat ID
    const fetchCat = async () => {
      try {
        const response = await axios.get(`https://api.thecatapi.com/v1/images/${id}`);
        setCat(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchCat();
  }, [id]);

  // Handle "Back to Homepage" button click
  const handleBackToHomepage = () => {
    history.goBack();
  };

  // Render cat data
  const renderCat = () => {
    if (!cat) {
      return <div>Loading cat...</div>;
    }

    return (
      <div>
        <h1>Single Cat Page</h1>
        <button onClick={handleBackToHomepage}>Back to Homepage</button>
        <h2>Breed: {breed}</h2>
        <div>
          <img src={cat.url} alt={`Cat ${cat.id}`} width="300" height="200" />
          <p>ID: {cat.id}</p>
          <p>URL: {cat.url}</p>
          <p>Width: {cat.width}</p>
          <p>Height: {cat.height}</p>
          <Link to="/">Back to Homepage</Link>
        </div>
      </div>
    );
  };

  return renderCat();
};

export default CatSinglePage;
