import React, { useContext, useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { AppContext } from './App';

const Homepage = () => {
  const { breed, setBreed } = useContext(AppContext);
  const [cats, setCats] = useState([]);
  const [page, setPage] = useState(1);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Fetch cats based on breed and page number
    const fetchCats = async () => {
      setIsLoading(true);

      try {
        const response = await axios.get(`https://api.thecatapi.com/v1/images/search?breed_id=${breed}&limit=10&page=${page}`);
        setCats(prevCats => [...prevCats, ...response.data]);
        setIsLoading(false);
      } catch (error) {
        console.error(error);
        setIsLoading(false);
      }
    };

    // Only fetch cats if breed is selected
    if (breed) {
      fetchCats();
    } else {
      // If breed is not selected, reset cats and page
      setCats([]);
      setPage(1);
    }
  }, [breed, page]);

  // Render cat cards
  const renderCats = () => {
    if (!breed) {
      return <div>Please select a breed from the dropdown.</div>;
    }

    if (isLoading) {
      return <div>Loading cats...</div>;
    }

    if (cats.length === 0) {
      return <div>No cats found for the selected breed.</div>;
    }

    return cats.map(cat => (
      <div key={cat.id}>
        <img src={cat.url} alt={`Cat ${cat.id}`} width="300" height="200" />
        <p>ID: {cat.id}</p>
        <Link to={`/cat/${cat.id}`}>View details</Link>
      </div>
    ));
  };

  // Load more cats
  const loadMoreCats = () => {
    setPage(prevPage => prevPage + 1);
  };

  // Handle breed selection
  const handleBreedChange = (e) => {
    setBreed(e.target.value);
    setCats([]);
    setPage(1);
  };

  return (
    <div>
      <h1>Homepage</h1>
      <select value={breed} onChange={handleBreedChange}>
        <option value="">-- Select Breed --</option>
        <option value="abys">Abyssinian</option>
        <option value="aege">Aegean</option>
        <option value="abob">American Bobtail</option>
        {/* Add more breed options here */}
      </select>
      <div>{renderCats()}</div>
      {!isLoading && cats.length > 0 && (
        <button onClick={loadMoreCats}>Load more</button>
      )}
    </div>
  );
};

export default Homepage;
