import React, { useContext, useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { AppContext } from '../App';

const Homepage = () => {
  const { breed, setBreed } = useContext(AppContext);
  const [breeds, setBreeds] = useState([]);
  const [cats, setCats] = useState([]);
  const [page, setPage] = useState(1);
  const [isLoading, setIsLoading] = useState(true);
  const [loadedCats, setLoadedCats] = useState(0);
  const [totalResults, setTotalResults] = useState(0);

  useEffect(() => {
    // Fetch all available breeds
    const fetchBreeds = async () => {
      try {
        const response = await axios.get('https://api.thecatapi.com/v1/breeds');
        setBreeds(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchBreeds();
  }, []);


  useEffect(() => {
    // Fetch cats based on breed and page number
    const fetchCats = async () => {
      setIsLoading(true);

      try {
        const response = await axios.get(`https://api.thecatapi.com/v1/images/breed_id=${breed}&limit=2&page=${page}`);
        const newCats = response.data.filter(cat => !cats.some(existingCat => existingCat.id === cat.id)); // Filter out duplicates
        setCats(prevCats => [...prevCats, ...newCats]);
        setLoadedCats(prevLoadedCats => prevLoadedCats + newCats.length);
        setTotalResults(response.headers['pagination-count']); // Update total results count
        setIsLoading(false);
      } catch (error) {
        console.error(error);
        setIsLoading(false);
      }
    };

    // Only fetch cats if breed is selected
    if (breed) {
      fetchCats();
    } else {
      setCats([]);
      setPage(1);
      setLoadedCats(0);
    }
  }, [breed,page]);

  const renderCats = () => {
    if (!breed) {
      return <div>Please select a breed from the dropdown.</div>;
    }

    if (isLoading) {
      return <div>Loading cats...</div>;
    }

    if (cats.length === 0) {
      return <div>No cats found for the selected breed.</div>;
    }

    return cats.map(cat => (
      <div key={cat.id}>
        <img src={cat.url} alt={`Cat ${cat.id}`} width="300" height="200" />
        <Link to={`/${cat.id}`}>View details</Link>
      </div>
    ));
  };

  const loadMoreCats = () => {
    setPage(prevPage => prevPage + 1);
  };

  const allCatsLoaded = loadedCats >= totalResults;
  const showLoadMore = cats.length >= 10 && !allCatsLoaded; // Show "Load More" button only if there are more than 10 results

  const handleBreedChange = (e) => {
    setBreed(e.target.value);
    setCats([]);
    setPage(1);
    setLoadedCats(0);
  };

  return (
    <div>
      <h1>Homepage</h1>
      <select value={breed} onChange={handleBreedChange}>
        <option value="">-- Select Breed --</option>
        {breeds.map(breed => (
          <option key={breed.id} value={breed.id}>{breed.name}</option>
        ))}
      </select>
      <div>
        {renderCats()}
      </div>
      {showLoadMore &&
        <button onClick={loadMoreCats} disabled={isLoading}>
          {isLoading ? 'Loading...' : 'Load More'}
        </button>
      }
    </div>
  );
};

export default Homepage;
