import React, { useContext, useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { AppContext } from '../App';

const Homepage = () => {
  const { breed, setBreed } = useContext(AppContext);
  const [breeds, setBreeds] = useState([]);
  const [cats, setCats] = useState([]);
  const [page, setPage] = useState(1);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Fetch all available breeds
    const fetchBreeds = async () => {
      try {
        const response = await axios.get('https://api.thecatapi.com/v1/breeds');
        setBreeds(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchBreeds();
  }, []);

  useEffect(() => {
    // Fetch cats based on breed and page number
    const fetchCats = async () => {
      setIsLoading(true);

      try {
        const response = await axios.get(`https://api.thecatapi.com/v1/images/search?breed_id=${breed}&limit=10&page=${page}`);
        setCats(prevCats => [...prevCats, ...response.data]);
        setIsLoading(false);
      } catch (error) {
        console.error(error);
        setIsLoading(false);
      }
    };

    // Only fetch cats if breed is selected and cats array is empty
    if (breed && cats.length === 0) {
    setCats([]);
    setPage(1);
      fetchCats();
    } else {
      // If breed is not selected, reset cats and page
      setCats([]);
      setPage(1);
    }
  }, [breed]);

  // Render cat cards
  const renderCats = () => {
    if (!breed) {
      return <div>Please select a breed from the dropdown.</div>;
    }

    if (isLoading) {
      return <div>Loading cats...</div>;
    }

    if (cats.length === 0) {
      return <div>No cats found for the selected breed.</div>;
    }

    return cats.map(cat => (
      <div key={cat.id}>
        <img src={cat.url} alt={`Cat ${cat.id}`} width="300" height="200" />
        <p>ID: {cat.id}</p>
        <Link to={`/${cat.id}`}>View details</Link>
      </div>
    ));
  };

  // Load more cats
  const loadMoreCats = () => {
    setPage(prevPage => prevPage + 1);
  };

  // Handle breed selection
  const handleBreedChange = (e) => {
    setBreed(e.target.value);
    setCats([]);
    setPage(1);
  };

  const showLoadMore = cats.length > 10;

  return (
    <div>
      <h1>Homepage</h1>
      <select value={breed} onChange={handleBreedChange}>
        <option value="">Select Breed</option>
        {breeds.map(b => (
          <option key={b.id} value={b.id}>{b.name}</option>
        ))}
      </select>
      <div>{renderCats()}</div>
      {showLoadMore && (
        <button onClick={loadMoreCats}>Load More</button>
      )}
    </div>
  );
};

export default Homepage;
