import React, { useState, useEffect, useContext } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { AppContext } from '../App';

const HomePage = () => {
  const [breeds, setBreeds] = useState([]);
  const [selectedBreed, setSelectedBreed] = useContext(AppContext);
  const [catImages, setCatImages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch list of cat breeds on component mount
    const fetchBreeds = async () => {
      try {
        const response = await axios.get('https://api.thecatapi.com/v1/breeds');
        setBreeds(response.data);
      } catch (error) {
        setError('Failed to fetch cat breeds. Please try again later.');
      }
    };
    fetchBreeds();
  }, []);

  const handleBreedSelect = async (event) => {
    const selectedBreedId = event.target.value;
    setSelectedBreed(selectedBreedId); // Update selected breed in the context
    setCatImages([]); // Clear previous cat images
    setLoading(true); // Show loading spinner

    try {
      // Fetch images of cats based on selected breed
      const response = await axios.get(`https://api.thecatapi.com/v1/images/search?breed_ids=${selectedBreedId}&limit=10`);
      setCatImages(response.data);
    } catch (error) {
      setError('Failed to fetch cat images. Please try again later.');
    } finally {
      setLoading(false); // Hide loading spinner
    }
  };

  const handleLoadMore = async () => {
    setLoading(true); // Show loading spinner

    try {
      // Fetch more images of cats based on selected breed
      const response = await axios.get(`https://api.thecatapi.com/v1/images/search?breed_ids=${selectedBreed}&limit=10`);
      setCatImages([...catImages, ...response.data]);
    } catch (error) {
      setError('Failed to fetch more cat images. Please try again later.');
    } finally {
      setLoading(false); // Hide loading spinner
    }
  };

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h1>Cat Browser</h1>
      <p>Select a breed</p>
      <select>
          <option>sa</option>
      </select>

    </div>
  );
};

export default HomePage
