import React, { useEffect, useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import axios from 'axios';
import { AppContext } from '../App';

const CatSinglePage = () => {
const { catId } = useParams();
const [cat, setCat] = useState(null);
const { breed, setBreed } = useContext(AppContext);

useEffect(() => {
  // Fetch cat data based on cat ID
  const fetchCat = async () => {
      try {
      const response = await axios.get(`https://api.thecatapi.com/v1/images/${catId}`);
      setCat(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  fetchCat();
}, [catId]);

// Render cat data
const renderCat = () => {
  if (!cat) {
    return <div>Loading cat...</div>;
  }

  return (
    <div>
      <Link to={`/`}>Back to Homepage</Link>
      <h2>Breed: {cat.breeds[0].name}</h2>
      <div>
        <figure className="cat__img-wrapper">
          <img className="cat__img" src={cat.url} alt={`Cat ${cat.id}`} width="300" height="200" />
        </figure>
        <p className="cat__origin">Origin: {cat.breeds[0].origin}</p>
        <p className="cat__temperament">{cat.breeds[0].temperament}</p>
        <p className="cat__description">{cat.breeds[0].description}</p>
      </div>
    </div>
  );
};

return renderCat();
};

export default CatSinglePage;
