import React, { useContext, useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { AppContext } from '../App';

const CatSinglePage = (props) => {
  const { catId } = props.match.params;
  const history = useNavigate();
  const { setBreed } = useContext(AppContext);
  const [cat, setCat] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Fetch cat details by catId
    const fetchCat = async () => {
      setIsLoading(true);

      try {
        const response = await axios.get(`https://api.thecatapi.com/v1/images/${catId}`);
        setCat(response.data);
        setIsLoading(false);
      } catch (error) {
        console.error(error);
        setIsLoading(false);
      }
    };

    fetchCat();
  }, [catId]);

  // Handle "Back" button click
  const handleBackClick = () => {
    setBreed(null); // Reset selected breed in AppContext
    history.goBack(); // Go back to previous page
  };

  // Render cat details
  const renderCatDetails = () => {
    if (isLoading) {
      return <div>Loading cat details...</div>;
    }

    if (!cat) {
      return <div>No cat details found.</div>;
    }

    return (
      <div>
        <h1>Cat Details</h1>
        <div>
          <img src={cat.url} alt={`Cat ${cat.id}`} width="300" height="200" />
          <p>ID: {cat.id}</p>
          <p>Breed: {cat.breeds.length > 0 ? cat.breeds[0].name : 'Unknown'}</p>
          <p>Life Span: {cat.breeds.length > 0 ? cat.breeds[0].life_span : 'Unknown'}</p>
          <p>Description: {cat.breeds.length > 0 ? cat.breeds[0].description : 'Unknown'}</p>
          <Link to="/" onClick={handleBackClick}>Back</Link>
        </div>
      </div>
    );
  };

  return (
    <div>
      {renderCatDetails()}
    </div>
  );
};

export default CatSinglePage;
