import React, { useContext, useEffect, useState } from 'react';
import { AppContext } from '../App';
import axios from 'axios';

const CatSinglePage = ({ match, history }) => {
  const { selectedBreed } = useContext(AppContext);
  const [cat, setCat] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchCat = async () => {
      setIsLoading(true);

      try {
        const response = await axios.get(`https://api.thecatapi.com/v1/images/${match.params.id}`);
        setCat(response.data);
        setIsLoading(false);
      } catch (error) {
        console.error(error);
        setIsLoading(false);
      }
    };

    fetchCat();
  }, [match.params.id]);

  const handleBack = () => {
    history.push('/');
  };

  if (isLoading) {
    return <div>Loading cat details...</div>;
  }

  if (!cat) {
    return <div>Failed to load cat details.</div>;
  }

  return (
    <div>
      <h1>Cat Details</h1>
      <img src={cat.url} alt={`Cat ${cat.id}`} width="300" height="200" />
      <p>ID: {cat.id}</p>
      <p>Breed: {selectedBreed}</p>
      <p>Life Span: {cat.breeds.length > 0 ? cat.breeds[0].life_span : 'Unknown'}</p>
      <button onClick={handleBack}>Back</button>
    </div>
  );
};

export default CatSinglePage;
